import firebase from 'firebase';
import React, { Component } from 'react';
import './style.css'
import {firebaseConfig} from '../../../configFire'
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    Legend
  } from 'recharts'



export default class Apss extends Component{
    constructor(){
        super()
        this.app = firebase.initializeApp(firebaseConfig);
        this.state = {
            umid:"",
            temp:"",
            date:"",
            data:[
                {name:this.date, pv:this.umid, arm:""},
            ]
        }
    }
    componentDidMount(){
        this.database = this.app.database().ref().child('umi');
        this.database.on("value", snap => {
            this.setState({
                umid: snap.val(),
                date: new Date().toString()
            });
        });
        this.database = this.app.database().ref().child('temp');
        this.database.on("child_added", snap => {
            this.setState({
                temp: snap.val(),
                data: [{name:this.state.date, pv:this.state.umid, amt:this.state.temp}]
            });
        });

    }

    render(){
        return(
            <div>
                <h1 id="text_umi">Umidade: {this.state.umid}</h1>
                <h1 id="text_temp">Temperatura: {this.state.temp}</h1>
                <h1 id="text_data">Data: {this.state.date}</h1> 
                <LineChart
        width={600}
        height={300}
        data={this.state.data}
        margin={{top: 5, right: 30, left: 20, bottom: 5}}
        >
        <Line
          type='monotone'
          dataKey='pv'
          stroke='#8884d8'
          activeDot={{r: 8}}
          />
        <CartesianGrid strokeDasharray='3 3'/>
        <Tooltip/>
        <YAxis/>
        <XAxis dataKey='name'/>
        <Legend />
      </LineChart>

            </div> 
        )
    }
}
