                <h1>Real Time CPU Usage</h1>
      <LineChart width={500} height={300} data={this.state.data}>
        <XAxis dataKey="name" />
        <YAxis />
        <Line dataKey="pv" />
      </LineChart>

            </div> 
        )
    }
}
