            umid:[], 
            temp:[],
            date:[]
        };

    }
    componentDidMount(){
        this.loadData();
    }

    loadData = () => {
        TWEETSREF.on("value", (snapshot)=> {
            console.log(snapshot.val());
            this.setState({umid:  snapshot.val().umidade});
            this.setState({temp:  snapshot.val().temperature});
            this.setState({date:  snapshot.val().dateRegister})
    })
    };

    render(){
        return (
            <div className="dataList">
            <h1>{this.state.umid}</h1>
            <h1>{this.state.temp}</h1>
            <h1>{this.state.date}</h1>
            </div>
        ) 
    }
}
